export default function WinnerModal() {
    return (
        <>
        { winner !== null && (
        <section className='winner'>
          <div className='text'>
            <h2>
              {
                winner === false ? 'Draw' : 'The winner is: '
              } 
            </h2>

            <header className='win'>
              {winner && <Square>{winner}</Square>}
            </header>

            <footer>
              <button onClick={restartGame}>Play Again</button>
            </footer>
          </div>
        </section>
      )
    }    
        </>
        )
}
